import './Form.module.css'
import {useState,useRef} from "react"
import Validate from "./validation.js"
import video from "../../videos/videoform.mp4"

export default function Form (props){

  const{login,SubmitSound,access}=props
    const [user,setData]=useState(
        {email:"",
        password:"",
    })

    const{clase,setClase}=useState('forms')

    const [errors,setErrors]=useState({})

    function handleSubmit(e){
      SubmitSound.play()
      e.preventDefault()
      login(user)
    }

    function handleChange(e){
        const property=e.target.value
        const name=e.target.name
        setData({...user,[name]:property})
        setErrors(Validate({...user,[name]:property}))

    }

    const videoRef = useRef(null);

    const playVideo = () => {
      videoRef.current.play();
    };



    return <div className='contenedor'>

             <video ref={videoRef} onEnded={""}>
             <source src={video} type="video/mp4" />
              Tu navegador no soporta la reproducci√≥n de videos HTML5.
             </video>
            {access && (

              playVideo())}


             <form className={clase}>

               <div className='labelform1'>
               <label>Email</label>
               <input name="email" onChange={handleChange} />
               <p className='p1'>{errors.email}</p>
               </div>
               <div className='labelform2'>
               <label>Password</label>
               <input type="password" name="password" onChange={handleChange}/>
               <p className='p1'>{errors.password}</p>
               </div>
               <button onClick={handleSubmit}>Submit</button>

             </form>

           </div>;

}
