import React from "react";
import SearchBar from "../SearchBar/SearchBar";
import { Link } from "react-router-dom";
import style from '../NavBar/NavBar.module.css'
import { useSelector } from "react-redux";
import { useLocation } from "react-router-dom";


const NavBar = (props) => {
    const location=useLocation()
    const { onSearch, SubmitSound } = props;
    const myFavorites=useSelector(state=>state.myFavorites)
    return <div className={style.nav}>
        <SearchBar SubmitSound={SubmitSound} onSearch={onSearch} />
        <Link to='/about'>
            < button  id={style.bt1} onClick={()=>{SubmitSound.play()}}>
                <span > About
                </span></button>
        </Link>
        <Link to='/home'>
            <button id={style.bt2} onClick={()=>{SubmitSound.play()}}>
                <span > Home
                </span></button>
        </Link>
        <Link to='/Favorites'>
            <button id={style.bt3} onClick={()=>{SubmitSound.play()}}>
                <span > ü§ç
                </span>
                <span >{myFavorites.length}</span>
                </button>
        </Link>

    </div>
}
export default NavBar;
