import{ADD_FAV,REMOVE_FAV} from './types'
import axios from 'axios';

const URL='http://localhost:3001/fav'

export const addFav=(char)=>{    // es una accion asiNcrona y retornamos una funcion con dispatch
    return function(dispatch){
        axios(`${URL}`, char)    // le enviamos la url y necesitamos mandarle la informacion porque es un metodo POST que esta esperando un body, es este caso "char"
        .then(({data})=>{
            return dispatch(
                {
                    type: ADD_FAV,
                    payload: data
                }
            )
            })}
    }

export const removeFav=(id)=>{
    return (dispatch)=>{
        axios(`${URL}/${id}`)
        .then(({data})=>{
            return dispatch(   {
                type: REMOVE_FAV,
                payload: data
            })
        })

}
}
