import style from "../Form/Form.module.css"
import {useState,useRef} from "react"
import Validate from "./validation.js"

export default function Form (props){
  const{login,SubmitSound}=props
    const [user,setData]=useState(
        {email:"",
        password:"",
    })

    const videoRef = useRef(null)

    const playVideo = () => {
      videoRef.current.play();
    };

    const [errors,setErrors]=useState({})

    function handleSubmit(e){
      SubmitSound.play()
      e.preventDefault()
      login(user)
    }

    function handleChange(e){
        const property=e.target.value
        const name=e.target.name
        setData({...user,[name]:property})
        setErrors(Validate({...user,[name]:property}))

    }



    return <div className={style.contenedor}>
             <video ref={videoRef} controls>
             <source src="../../../img/videoform.mp4" type="video/mp4" />
             {playVideo()}
              Tu navegador no soporta la reproducci√≥n de videos HTML5.
             </video>
             <form className={style.forms}>
               <div className={style.labelform1}>
               <label>Email</label>
               <input name="email" onChange={handleChange} />
               <p className={style.p1}>{errors.email}</p>
               </div>
               <div className={style.labelform2}>
               <label>Password</label>
               <input type="password" name="password" onChange={handleChange}/>
               <p className={style.p1}>{errors.password}</p>
               </div>
               <button onClick={handleSubmit}>Submit</button>
             </form>
           </div>;

}
