//Commons imports
import { useState,useEffect} from 'react';
import axios from 'axios';
import { Route, Routes, useLocation,useNavigate } from 'react-router-dom';
//Styles
import './App.css';
//Components
import Cards from '../src/components/Cards/Cards'
import NavBar from './components/NavBar/NavBar';
import About from './components/About/About';
import Detail from './components/Detail/Detail';
import Form from './components/Form/Form';



function App(props) {

   const navigate = useNavigate()
   const[access,setAccess] =useState(false)
   const EMAIL="rick@gmail.com"
   const PASSWORD="rick123"

   function login(userData) {

      console.log(userData.password)
      if (userData.password === PASSWORD && userData.email === EMAIL) {

         setAccess(true);
         navigate('/home');
      }
   }

   useEffect(() => {

      !access && navigate('/');
   }, [access,navigate]);

   const[characters,setCharacters]=useState([])
   const[Id,setId]=useState([])

   const onSearch=(id)=>{

      let array=[]
      array=Id.filter((char)=>char===id)

      if(array.length===0){     

      //solicitud a la API
      axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {

       if (data.name) {
       setCharacters((oldChars) => [...oldChars, data]);
       setId((Id) =>[...Id, id])
        } else {
       window.alert('¡No hay personajes con este ID!');
       }
       } );
       //
      }

   }   




   const onClose =(id)=>{
      var audio = document.getElementById("audio");
      audio.play();

      const characterfiltered=characters.filter((char)=>{return char.id !== Number(id)
      })
      setCharacters(characterfiltered)
      const idfiltrado=Id.filter((char)=>{return char===id})
      console.log(idfiltrado)
      setId(idfiltrado)
      //setCharacters(characters.filter((char)=>{char.id!==Number(id)}))
   }


   const location=useLocation()
   const nav=()=>{
   if(location.pathname!=="/") {return <NavBar onSearch={onSearch}/>}
   }

   return (


        <div className='App'>
           <audio id="audio" controls>
           <source type="audio/wav" src="audio.wav"/>
           </audio>
           {nav()}

           <Routes>
            <Route path='/' element={<Form login={login}/>}/>
            <Route path="/home" element={<Cards characters={characters} onClose={onClose}/>} />
            <Route path="/About" element={<About/>}/>
            <Route path="/detail/:id" element={<Detail/>}/>
           </Routes>

        </div>
   );
}

export default App;
